/**
 * Every word that follows "var" is a variable. it's like a box with a name on it. This box will contain some data. It can be a word, a number, or an HTML object like the tag canvas
 * Below you will find all the variable "boxes" that contain the information for the game to behave and work in a certain way.
 */

//######################## BEGINNING OF THE VARIABLES SETTINGS #################################
// this line will get the tag <canvas> in the HTML file so we can use it
var canvas = document.getElementById("myCanvas");
// this line tells that we will create an 2D game. 3D will be when you know more math ;-)
var ctx = canvas.getContext("2d");
// Define the radius of a ball
var ballRadius = 7;
// Horizontal position of the ball when we start
var x = canvas.width / 2;
// Vertical position of the ball when we start
var y = canvas.height - 30;
// Horizontal speed of the ball. Increase for more stamina
var dx = 3.5;
// Vertical speed ot the ball. Increase for more madness
var dy = -3.5;
// The height of the paddle.
var paddleHeight = 10;
// The width of the paddle. Don't make it to big you cheater  !!! :-)
var paddleWidth = 105;
// The Horizontal position of the paddle in the beginning
var paddleX = (canvas.width - paddleWidth) / 2;
// Setting the state of the right arrow key in the beginning of the game
var rightPressed = false;
// Setting the state of the left arrow key in the beginning of the game
var leftPressed = false;
// Number of row of brick
var brickRowCount = 21;
// Number of column of brick
var brickColumnCount = 5;
// The width of a brick. Make it smaller to improve your aiming then you can increase the number of rows and column of bricks
var brickWidth = 75;
// The width of a brick. Make it smaller to improve your aiming then you can increase the number of rows and column of bricks
var brickHeight = 20;
// The space between each brick
var brickPadding = 11;
// The space between the top and the bricks
var brickOffsetTop = 30;
// The space between the left wall and the bricks
var brickOffsetLeft = 30;
// The score of the beginning
var score = 0;
// The number of lives you start with. Decrease to one... it will be insane mouahahahaha
var lives = 3;
// The message that you will receive when you win
var messageWin = "YOU WIN, CONGRATS!";
// The message that you will receive when you lose
var messageGameOver = "GAME OVER";
// The color of the ball
var ballColor = "#0095DD";
// The color of the paddle
var paddleColor = "#0095DD";
// The color of the brick
var brickColor = getRandomColor()
// The color of the score
var scoreColor = "#0095DD";
// The color of the lives
var liveColor = "#0095DD";
// CODE TO PAUSE 1/3
var pause = false;
var dx_before_pause = dx;
var dy_before_pause = dy;
//######################## END OF THE VARIABLES SETTINGS #################################______________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________